def differentiate(equation, point):    equation = equation.replace('-', '+-').split('+')    result = []    for i in equation:        if 'x^' in i:            i = i.split('x^')            if not i[0]:                i[0] = 0                i[0] += 1            elif i[0] == '-':                i[0] = -1            result.append(int(i[0]) * int(i[1]) * (point ** (int(i[1]) - 1)))        elif 'x' in i:            i = i.replace('x', '')            if not i:                i = 1            elif i == '-':                i = -1            result.append(int(i))    print(result)differentiate('-x^2+3x-3', 3)